标准令牌合约可能相当复杂。
但实质上，一个非常基本的令牌归结为：  !!! include ( solidity / token - minimal . sol ) !!!  contract   MyToken   { 
     /* This creates an array with all balances */ 
     mapping   ( address   =   uint256 )   public   balanceOf ; 

     /* Initializes contract with initial supply tokens to the creator of the contract */ 
     function   MyToken ( 
         uint256   initialSupply 
         )   { 
         balanceOf [ msg . sender ]   =   initialSupply ;                // Give the creator all initial tokens 
     } 

     /* Send coins */ 
     function   transfer ( address   _to ,   uint256   _value )   { 
         require ( balanceOf [ msg . sender ]   =   _value );             // Check if the sender has enough 
         require ( balanceOf [ _to ]   +   _value   =   balanceOf [ _to ]);   // Check for overflows 
         balanceOf [ msg . sender ]   -=   _value ;                      // Subtract from the sender 
         balanceOf [ _to ]   +=   _value ;                             // Add the same to the recipient 
     }  }

但是如果你只想复制粘贴更完整的代码，那么使用下面的代码：  !!! include ( solidity / token - erc20 . sol ) !!!  pragma   solidity   ^ 0.4.16 ;  interface   tokenRecipient   {   function   receiveApproval ( address   _from ,   uint256   _value ,   address   _token ,   bytes   _extraData )   public ;   }  contract   TokenERC20   { 
     // Public variables of the token 
     string   public   name ; 
     string   public   symbol ; 
     uint8   public   decimals   =   18 ; 
     // 18 decimals is the strongly suggested default, avoid changing it 
     uint256   public   totalSupply ; 

     // This creates an array with all balances 
     mapping   ( address   =   uint256 )   public   balanceOf ; 
     mapping   ( address   =   mapping   ( address   =   uint256 ))   public   allowance ; 

     // This generates a public event on the blockchain that will notify clients 
     event   Transfer ( address   indexed   from ,   address   indexed   to ,   uint256   value ); 

     // This notifies clients about the amount burnt 
     event   Burn ( address   indexed   from ,   uint256   value ); 

     /**       * Constructor function       *       * Initializes contract with initial supply tokens to the creator of the contract       */ 
     function   TokenERC20 ( 
         uint256   initialSupply , 
         string   tokenName , 
         string   tokenSymbol 
     )   public   { 
         totalSupply   =   initialSupply   *   10   **   uint256 ( decimals );    // Update total supply with the decimal amount 
         balanceOf [ msg . sender ]   =   totalSupply ;                  // Give the creator all initial tokens 
         name   =   tokenName ;                                     // Set the name for display purposes 
         symbol   =   tokenSymbol ;                                 // Set the symbol for display purposes 
     } 

     /**       * Internal transfer, only can be called by this contract       */ 
     function   _transfer ( address   _from ,   address   _to ,   uint   _value )   internal   { 
         // Prevent transfer to 0x0 address. Use burn() instead 
         require ( _to   !=   0x0 ); 
         // Check if the sender has enough 
         require ( balanceOf [ _from ]   =   _value ); 
         // Check for overflows 
         require ( balanceOf [ _to ]   +   _value     balanceOf [ _to ]); 
         // Save this for an assertion in the future 
         uint   previousBalances   =   balanceOf [ _from ]   +   balanceOf [ _to ]; 
         // Subtract from the sender 
         balanceOf [ _from ]   -=   _value ; 
         // Add the same to the recipient 
         balanceOf [ _to ]   +=   _value ; 
         Transfer ( _from ,   _to ,   _value ); 
         // Asserts are used to use static analysis to find bugs in your code. They should never fail 
         assert ( balanceOf [ _from ]   +   balanceOf [ _to ]   ==   previousBalances ); 
     } 

     /**       * Transfer tokens       *       * Send `_value` tokens to `_to` from your account       *       * @param _to The address of the recipient       * @param _value the amount to send       */ 
     function   transfer ( address   _to ,   uint256   _value )   public   { 
         _transfer ( msg . sender ,   _to ,   _value ); 
     } 

     /**       * Transfer tokens from other address       *       * Send `_value` tokens to `_to` on behalf of `_from`       *       * @param _from The address of the sender       * @param _to The address of the recipient       * @param _value the amount to send       */ 
     function   transferFrom ( address   _from ,   address   _to ,   uint256   _value )   public   returns   ( bool   success )   { 
         require ( _value   =   allowance [ _from ][ msg . sender ]);       // Check allowance 
         allowance [ _from ][ msg . sender ]   -=   _value ; 
         _transfer ( _from ,   _to ,   _value ); 
         return   true ; 
     } 

     /**       * Set allowance for other address       *       * Allows `_spender` to spend no more than `_value` tokens on your behalf       *       * @param _spender The address authorized to spend       * @param _value the max amount they can spend       */ 
     function   approve ( address   _spender ,   uint256   _value )   public 
         returns   ( bool   success )   { 
         allowance [ msg . sender ][ _spender ]   =   _value ; 
         return   true ; 
     } 

     /**       * Set allowance for other address and notify       *       * Allows `_spender` to spend no more than `_value` tokens on your behalf, and then ping the contract about it       *       * @param _spender The address authorized to spend       * @param _value the max amount they can spend       * @param _extraData some extra information to send to the approved contract       */ 
     function   approveAndCall ( address   _spender ,   uint256   _value ,   bytes   _extraData ) 
         public 
         returns   ( bool   success )   { 
         tokenRecipient   spender   =   tokenRecipient ( _spender ); 
         if   ( approve ( _spender ,   _value ))   { 
             spender . receiveApproval ( msg . sender ,   _value ,   this ,   _extraData ); 
             return   true ; 
         } 
     } 

     /**       * Destroy tokens       *       * Remove `_value` tokens from the system irreversibly       *       * @param _value the amount of money to burn       */ 
     function   burn ( uint256   _value )   public   returns   ( bool   success )   { 
         require ( balanceOf [ msg . sender ]   =   _value );     // Check if the sender has enough 
         balanceOf [ msg . sender ]   -=   _value ;              // Subtract from the sender 
         totalSupply   -=   _value ;                        // Updates totalSupply 
         Burn ( msg . sender ,   _value ); 
         return   true ; 
     } 

     /**       * Destroy tokens from other account       *       * Remove `_value` tokens from the system irreversibly on behalf of `_from`.       *       * @param _from the address of the sender       * @param _value the amount of money to burn       */ 
     function   burnFrom ( address   _from ,   uint256   _value )   public   returns   ( bool   success )   { 
         require ( balanceOf [ _from ]   =   _value );                  // Check if the targeted balance is enough 
         require ( _value   =   allowance [ _from ][ msg . sender ]);      // Check allowance 
         balanceOf [ _from ]   -=   _value ;                           // Subtract from the targeted balance 
         allowance [ _from ][ msg . sender ]   -=   _value ;               // Subtract from the sender s allowance 
         totalSupply   -=   _value ;                                // Update totalSupply 
         Burn ( _from ,   _value ); 
         return   true ; 
     }  }

所以让我们从基础开始
打开电子钱包应用程序，转到合约选项卡，然后部署新合约。
在Solidity Contract源代码文本字段中，输入以下代码：       contract   MyToken   { 
         /* 这将创建一个包含所有余额的数组 */ 
         mapping   ( address   =   uint256 )   public   balanceOf ; 
     }   映射意味着一个关联数组，您将地址与余额相关联。
地址是基本的十六进制以太坊格式，余额是整数，范围从0到115 quattuorvigintillion。
如果你不知道quutuorvigintillion是多少，它比许多你打算使用你的代币更令人兴奋。
public*关键字, 意味着这个变量将被区块链上的任何人访问， 意味着所有的余额都是公开的(因为他们需要，为了让客户展示他们).   如果您立即发布您的合约， 它会起作用但不会非常有用： 这将是一个合约，可以查询您的硬币余额的任何地址 - 但因为你从来没有创建一个硬币， 他们中的每一个都会返回0。
所以我们将在启动时创建一些令牌。
在最后一个右括号之前添加此代码，位于 mapping .. 行下方。       function   MyToken ()   { 
         balanceOf [ msg . sender ]   =   21000000 ; 
     }   请注意， 函数MyToken  与  contract MyToken  具有相同的名称。
这非常重要，如果你重命名一个， 你必须重新命名另一个： 这是一个特殊的启动功能，仅在合约首次上传到网络时才运行一次。
该功能将设置  msg.sender  的平衡， 部署合约的用户，余额为2100万。  2,100万的选择是相当随意的， 你可以在代码中将其更改为任何你想要的东西， 但有一个更好的方法： 而是将其作为函数的参数提供，如下所示：       function   MyToken ( uint256   initialSupply )   public   { 
         balanceOf [ msg . sender ]   =   initialSupply ; 
     }   看看合约旁边的右栏，你会看到一个下拉列表，写 pick a contract 。
选择 MyToken 合约，你会看到现在它显示了一个名为  Constructor parameters  的部分。
这些是令牌的可变参数，因此您可以重复使用相同的代码，并且将来只能更改这些变量。   现在你有一个创建令牌余额的功能合约，但由于没有任何功能来移动它，它所做的只是保留在同一个帐户上。
所以我们现在要实施。
在最后一个括号之前写下以下代码。       /* 发送硬币 */ 
     function   transfer ( address   _to ,   uint256   _value )   { 
         /* 加减新的余额 */ 
         balanceOf [ msg . sender ]   -=   _value ; 
         balanceOf [ _to ]   +=   _value ; 
     }   这是一个非常简单的功能: 它有一个接收者和一个值作为参数，并且每当有人调用它的时候, 它会从他们的余额中减去  _value  并将其添加到  _to  余额中.
马上就有一个明显的问题: 如果这个人想要发送比它拥有更多的东西会发生什么？
由于我们不想在这份特定的合约中处理债务， 我们只需要做一个快速检查，如果发件人没有足够的资金，合约执行就会停止。
这也是检查溢出， 以避免有一个数量如此之大以至于再次变为零。  要在执行中止时停止执行合约，您可以 return 或者 throw 
前者将减少天然气成本，但可能会更加令人头疼，因为迄今为止对合约所做的任何更改都将保留。
另一方面， 'throw'将取消所有合约执行， 恢复交易可能发生的任何变化，并且发件人将失去他为天然气发送的所有以太网。
但是，由于电子钱包可以检测到合约将抛出, 它总是显示一个警报, 因此阻止任何乙醚被花费。       function   transfer ( address   _to ,   uint256   _value )   { 
         /* 检查发件人是否有余额和溢出 */ 
         require ( balanceOf [ msg . sender ]   =   _value     balanceOf [ _to ]   +   _value   =   balanceOf [ _to ]); 

         /* 加减新的余额 */ 
         balanceOf [ msg . sender ]   -=   _value ; 
         balanceOf [ _to ]   +=   _value ; 
     }   现在缺少的是有关合约的一些基本信息。
在不久的将来，这可以通过令牌注册表来处理， 但现在我们将把它们直接添加到合约中：  string public name;
string public symbol;
uint8 public decimals;  现在我们更新 constructor function 以允许在开始时设置所有这些变量：       /* 将初始供应令牌初始化为合约的创建者 */ 
     function   MyToken ( uint256   initialSupply ,   string   tokenName ,   string   tokenSymbol ,   uint8   decimalUnits )   { 
         balanceOf [ msg . sender ]   =   initialSupply ;                // 为创建者提供所有初始令牌 
         name   =   tokenName ;                                     // 为显示目的设置名称 
         symbol   =   tokenSymbol ;                                 // 设置符号用于显示目的 
         decimals   =   decimalUnits ;                              // 用于显示的小数位数 
     }   最后，我们现在需要一个名为 Events 的东西。
这些是特殊的空白功能，您可以致电以帮助像以太坊钱包这样的客户跟踪合约中发生的活动。
活动应以大写字母开头。
在合约开始处添加此行以声明事件：       event   Transfer ( address   indexed   from ,   address   indexed   to ,   uint256   value );   然后你只需要在 transfer 函数中添加这两行：           /* Notify anyone listening that this transfer took place */ 
         Transfer ( msg . sender ,   _to ,   _value );   现在你的代币已经准备好了！

所有的dapps默认都是完全分散的, 但这并不意味着他们不能拥有某种中央经理, 如果你想要他们.
也许你想要能够铸造更多的硬币, 也许你想要能够铸造更多的硬币.
您可以添加任何这些功能, 但问题在于你只能在开始时添加它们, 所以所有的代币持有者在决定拥有游戏之前总是会准确地知道游戏的规则.  为了实现这一点，你需要一个货币中央控制器.
这可能是一个简单的帐户, 但也可能是合约，因此创建更多令牌的决定将取决于合约: 如果它是一个可以投票的民主组织, 或者它可能只是一种限制令牌所有者权力的方式.  为了做到这一点，我们将学习合约的一个非常有用的属性: inheritance .
继承允许合约获得父合约的财产, 而不必重新定义所有这些.
这使得代码更清晰，更易于重用.
将此代码添加到代码的第一行, 之前 contract MyToken { .       contract   owned   { 
         address   public   owner ; 

         function   owned ()   { 
             owner   =   msg . sender ; 
         } 

         modifier   onlyOwner   { 
             require ( msg . sender   ==   owner ); 
             _ ; 
         } 

         function   transferOwnership ( address   newOwner )   onlyOwner   { 
             owner   =   newOwner ; 
         } 
     }   这创建了一个非常基本的合约，除了定义一些关于可以 owned 的合约的通用函数之外，它什么也不做。
现在下一步就是将文本 is owned 添加到您的合约中：       contract   MyToken   is   owned   { 
         /* 合约的其余部分照常 */   这意味着现在 MyToken 中的所有函数都可以访问变量  owner  和修饰符  onlyOwner  。
合约也获得了转让所有权的职能。
因为在启动时设置合约的所有者可能很有趣, 你也可以把它添加到 constructor function :       function   MyToken ( 
         uint256   initialSupply , 
         string   tokenName , 
         uint8   decimalUnits , 
         string   tokenSymbol , 
         address   centralMinter 
         )   { 
         if ( centralMinter   !=   0   )   owner   =   centralMinter ; 
     }

假设你想要改变流通中的硬币数量。
当您的令牌实际上代表区块链资产并且您希望虚拟库存反映 真实资产 时，就是这种情况。
当货币持有者期望对令牌的价格进行某种控制时，情况也可能如此, 并想发行或删除流通中的令牌.  首先，我们需要添加一个变量来存储 totalSupply 并将其分配给我们的构造函数。       contract   MyToken   { 
         uint256   public   totalSupply ; 

         function   MyToken (...)   { 
             totalSupply   =   initialSupply ; 
             ... 
         } 
         ... 
     }   现在让我们添加一个新的函数，它将使所有者创建新的令牌：       function   mintToken ( address   target ,   uint256   mintedAmount )   onlyOwner   { 
         balanceOf [ target ]   +=   mintedAmount ; 
         totalSupply   +=   mintedAmount ; 
         Transfer ( 0 ,   owner ,   mintedAmount ); 
         Transfer ( owner ,   target ,   mintedAmount ); 
     }   注意函数名称末尾的修饰符 onlyOwner 。
这意味着该函数将在编译时被重写，以继承我们之前定义的 modifier onlyOwner 中的代码。
该函数的代码将插入修改器函数的下划线,这意味着此特定功能只能由设置为所有者的帐户调用。
只需将它添加到 owner 修饰符的合约中，您就可以创建更多的硬币。

根据您的使用情况， 您可能需要对谁可以或不可以使用您的令牌有一些监管障碍。
为此，您可以添加一个参数，使合约所有者能够冻结或解冻资产。  将此变量和函数添加到合约中的任何位置。
你可以把它们放在任何地方，但为了好的做法，我们建议你将映射与其他事件的映射和事件放在一起。       mapping   ( address   =   bool )   public   frozenAccount ; 
     event   FrozenFunds ( address   target ,   bool   frozen ); 

     function   freezeAccount ( address   target ,   bool   freeze )   onlyOwner   { 
         frozenAccount [ target ]   =   freeze ; 
         FrozenFunds ( target ,   freeze ); 
     }   使用此代码，默认情况下所有帐户都会解冻，但所有者可以通过调用 Freeze Account 将其中的任何帐户设置为冻结状态。
不幸的是，冻结没有实际效果，因为我们没有在转移函数中添加任何内容。
我们正在改变这一点：       function   transfer ( address   _to ,   uint256   _value )   { 
         require ( ! frozenAccount [ msg . sender ]);   现在任何被冻结的账户都会保持其资金不变，但无法移动。
所有帐户默认解冻，直到您冻结它们， 但是您可以轻松地将该行为恢复为白名单，然后您需要手动批准每个帐户。
只需将 frozenAccount 重命名为 approvedAccount 并将最后一行更改为：           require ( approvedAccount [ msg . sender ]);

到目前为止，您依靠实用程序和信任来评估您的令牌。
但是如果你想要的话，你可以通过创建一个能够以市场价值自动销售和购买的基金来让代币的价值得到以太（或其他代币）的支持。  首先，我们来设置买入和卖出的价格：       uint256   public   sellPrice ; 
     uint256   public   buyPrice ; 

     function   setPrices ( uint256   newSellPrice ,   uint256   newBuyPrice )   onlyOwner   { 
         sellPrice   =   newSellPrice ; 
         buyPrice   =   newBuyPrice ; 
     }   对于不经常更改的价格，这是可以接受的，因为每次新的价格变化都需要您执行交易并花费一点以太网。
如果您希望持有浮动价格，我们建议您调查 标准数据Feed  下一步是制定买入和卖出功能：       function   buy ()   payable   returns   ( uint   amount ){ 
         amount   =   msg . value   /   buyPrice ;                      // 计算金额 
         require ( balanceOf [ this ]   =   amount );                 // 检查它是否有足够的销售量 
         balanceOf [ msg . sender ]   +=   amount ;                    // 将该金额添加到买方的余额中 
         balanceOf [ this ]   -=   amount ;                          // 从卖家的余额中减去金额 
         Transfer ( this ,   msg . sender ,   amount );                 // 执行反映更改的事件 
         return   amount ;                                      // 结束功能并返回 
     } 

     function   sell ( uint   amount )   returns   ( uint   revenue ){ 
         require ( balanceOf [ msg . sender ]   =   amount );           // 检查发件人是否有足够的销售量 
         balanceOf [ this ]   +=   amount ;                          // 检查发件人是否有足够的销售量 
         balanceOf [ msg . sender ]   -=   amount ;                    // 减去卖家余额中的金额 
         revenue   =   amount   *   sellPrice ; 
         msg . sender . transfer ( revenue );                       // 向卖家发送乙醚：最后这样做是很重要的，以防止递归攻击 
         Transfer ( msg . sender ,   this ,   amount );                 // 执行反映变化的事件 
         return   revenue ;                                     // 结束功能并返回 
     }   请注意，这不会创建新的令牌，但会更改合约所拥有的余额。
合约既可以拥有自己的代币，也可以拥有合约的所有者，同时可以设定价格，或者在某些情况下创建新的代币（如果适用），它不会触及银行的代币或乙醚。
这个合约可以调动资金的唯一方式是通过出售和购买它们。  Note 买入和卖出 价格 不是在以太网中设定的，而是在  wei  系统的最低货币 (相当于欧元和美元的分，或比特币中的Satoshi).
一个以太是1000000000000000000 wei。
因此，当在Ether中设置令牌价格时，最后添加18个零。  在创建合约时， 发送足够的乙醚以便它可以回购市场上所有的代币 否则您的合约将破产，您的用户将无法出售其代币.  前面的例子当然描述了与单个中央买方和卖方签订的合约，更有趣的合约将允许任何人都可以出价不同的价格的市场，或者可能直接从外部来源加价。

每次，您在Ethereum上进行交易，您需要向该块矿工支付费用，以计算您的智能合约的结果。 虽然这可能在未来发生变化 , 目前，费用只能在Ether中支付，因此所有令牌的用户都需要它。
账户余额小于费用的账户被卡住，直到业主可以支付必要的费用。
但在某些用例中，您可能不希望用户考虑以太坊， 区块链或如何获得Ether， 所以一旦可能的方法就会让您的硬币在检测到天平的危险性很低时立即自动补充用户余额。  所以一旦可能的方法就会让您的硬币在检测到天平的危险性很低时立即自动补充用户余额。
如果您不知道任何值，请将其设置为 5 finney（0.005 Ether） 。       uint   minBalanceForAccounts ; 

     function   setMinBalance ( uint   minimumBalanceInFinney )   onlyOwner   { 
          minBalanceForAccounts   =   minimumBalanceInFinney   *   1   finney ; 
     }   然后，将此行添加到 transfer 功能，以便发件人退款：       /* 发送硬币 */ 
     function   transfer ( address   _to ,   uint256   _value )   { 
         ... 
         if ( msg . sender . balance     minBalanceForAccounts ) 
             sell (( minBalanceForAccounts   -   msg . sender . balance )   /   sellPrice ); 
     }   您也可以改变它，以便发件人向收件人支付费用：       /* 发送硬币 */ 
     function   transfer ( address   _to ,   uint256   _value )   { 
         ... 
         if ( _to . balance minBalanceForAccounts ) 
             _to . send ( sell (( minBalanceForAccounts   -   _to . balance )   /   sellPrice )); 
     }   这将确保没有收到该令牌的账户少于必要的以太网来支付费用。

如果你添加所有的高级选项，这就是最终代码的样子:   !!! include ( solidity / token - advanced . sol ) !!!  pragma   solidity   ^ 0.4.16 ;  contract   owned   { 
     address   public   owner ; 

     function   owned ()   public   { 
         owner   =   msg . sender ; 
     } 

     modifier   onlyOwner   { 
         require ( msg . sender   ==   owner ); 
         _ ; 
     } 

     function   transferOwnership ( address   newOwner )   onlyOwner   public   { 
         owner   =   newOwner ; 
