         bytes   transactionBytecode 
     ) 
         onlyMembers 
         returns   ( uint   proposalID ) 
     { 
         return   newProposal ( beneficiary ,   etherAmount   *   1   ether ,   jobDescription ,   transactionBytecode ); 
     } 

     /**       * Check if a proposal code matches       *       * @param proposalNumber ID number of the proposal to query       * @param beneficiary who to send the ether to       * @param weiAmount amount of ether to send       * @param transactionBytecode bytecode of transaction       */ 
     function   checkProposalCode ( 
         uint   proposalNumber , 
         address   beneficiary , 
         uint   weiAmount , 
         bytes   transactionBytecode 
     ) 
         constant 
         returns   ( bool   codeChecksOut ) 
     { 
         Proposal   storage   p   =   proposals [ proposalNumber ]; 
         return   p . proposalHash   ==   sha3 ( beneficiary ,   weiAmount ,   transactionBytecode ); 
     } 

     /**       * Log a vote for a proposal       *       * Vote `supportsProposal? in support of : against` proposal #`proposalNumber`       *       * @param proposalNumber number of proposal       * @param supportsProposal either in favor or against it       * @param justificationText optional justification text       */ 
     function   vote ( 
         uint   proposalNumber , 
         bool   supportsProposal , 
         string   justificationText 
     ) 
         onlyMembers 
     { 
         Proposal   storage   p   =   proposals [ proposalNumber ];   // Get the proposal 
         require ( p . voted [ msg . sender ]   !=   true );             // If has already voted, cancel 
         p . voted [ msg . sender ]   =   true ;                       // Set this voter as having voted 
         if   ( supportsProposal )   {                           // If they support the proposal 
             p . currentResult ++ ;                            // Increase score 
         }   else   {                                          // If they don t 
             p . currentResult -- ;                            // Decrease the score 
         } 

         // Create a log of this event 
         Voted ( proposalNumber ,    supportsProposal ,   msg . sender ,   justificationText ); 

         // If you can execute it now, do it 
         if   (   now     proposalDeadline ( proposalNumber ) 
               p . currentResult     0 
               p . proposalHash   ==   sha3 ( p . recipient ,   p . amount ,   ) 
               supportsProposal )   { 
             executeProposal ( proposalNumber ,   ); 
         } 
     } 

     function   proposalDeadline ( uint   proposalNumber )   constant   returns ( uint   deadline )   { 
         Proposal   storage   p   =   proposals [ proposalNumber ]; 
         uint   factor   =   calculateFactor ( uint ( p . currentResult ),   ( members . length   -   1 )); 
         return   p . creationDate   +   uint ( factor   *   minimumTime   *    1   minutes ); 
     } 

     function   calculateFactor ( uint   a ,   uint   b )   constant   returns   ( uint   factor )   { 
         return   2 ** ( 20   -   ( 20   *   a ) / b ); 
     } 

     /**       * Finish vote       *       * Count the votes proposal #`proposalNumber` and execute it if approved       *       * @param proposalNumber proposal number       * @param transactionBytecode optional: if the transaction contained a bytecode, you need to send it       */ 
     function   executeProposal ( uint   proposalNumber ,   bytes   transactionBytecode )   { 
         Proposal   storage   p   =   proposals [ proposalNumber ]; 

         require ( now   =   proposalDeadline ( proposalNumber )                                           // If it is past the voting deadline 
               p . currentResult     0                                                                // and a minimum quorum has been reached 
               ! p . executed                                                                        // and it is not currently being executed 
               checkProposalCode ( proposalNumber ,   p . recipient ,   p . amount ,   transactionBytecode ));    // and the supplied code matches the proposal... 


         p . executed   =   true ; 
         assert ( p . recipient . call . value ( p . amount )( transactionBytecode )); 

         // Fire Events 
         ProposalExecuted ( proposalNumber ,   p . currentResult ,   proposalDeadline ( proposalNumber )); 
     }  }

无论何时您在以太坊创建交易，返回的字符串都是 交易哈希 。
您可以使用这些来跟踪正在进行的交易，或者使用_eth.getTransaction()_和_eth.getTransactionReceipt_过去的事务花费的天然气量。
以下是如何使用它：  var   tx   =    web3 . eth . sendTransaction ( { from :   web3 . eth . accounts [ 0 ] ,   to :   web3 . eth . accounts [ 1 ] ,   value :   amount } );  web3 . eth . getTransaction ( tx );   如果交易已被提取，您可以使用以下方式查看收据：  web3.eth.getTransactionReceipt(tx);

